{
  "version": "0.2",
  "phases": {
    "install": {
      "runtime-versions": {
        "nodejs": "12",
        "python": "3.8"
      },
      "commands": [
        "npm install aws-cdk@latest",
        "npm update",
        "python -m pip install -U -r requirements.txt"
      ]
    },
    "build": {
      "commands": [
        "npx cdk diff --path-metadata false"
      ]
    }
  },
  "artifacts": {
    "base-directory": "cdk.out",
    "files": [
      "**/*"
    ]
  },
  "env": {
    "exported-variables": [
      "CODEBUILD_BUILD_ID",
      "CODEBUILD_BUILD_NUMBER"
    ]
  }
}

